sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue == 4 || newValue == 5 || newValue == 6) {
        if (g_form.getValue('close_notes') == '') {
            g_form.addInfoMessage('Please provide Close Notes!');
			g_form.setReadOnly('close_notes', false);
            g_form.setMandatory('close_notes', true);
            return false;
        }
    } else {
        g_form.setMandatory('close_notes', false);
		g_form.setReadOnly('close_notes', true);
        g_form.setValue('close_notes', "");
    }
}
