var GetFinanceTeamGroupMember = Class.create();
GetFinanceTeamGroupMember.prototype = {
    getFinanceTeamMember: function() {
        var x = 'd27110cc476231107b748a5f746d43b3';
        var user_array = [];
        var getMembers = new GlideRecord('sys_user_grmember');
        getMembers.addQuery('group', x);
        getMembers.query();
        while (getMembers.next()) {
            user_array.push(getMembers.user + '');
        }
        return 'sys_idIN' + user_array.toString();
    }
};
